mapper.select:
SELECT * FROM ( SELECT faq_sq, faq_tp_id, faq_title, faq_content, COUNT(DISTINCT faq_sq) OVER() AS tot_cnt, ROW_NUMBER() OVER(ORDER BY faq_sq ASC) AS rank FROM faq
SELECT * FROM ( SELECT faq_sq, faq_tp_id, faq_title, faq_content, COUNT(DISTINCT faq_sq) OVER() AS tot_cnt, ROW_NUMBER() OVER(ORDER BY faq_sq ASC) AS rank FROM faq WHERE faq_tp_id = #{faq_tp_id}
AND REGEXP_LIKE(faq_content, #{q}, 'i')
) WHERE ((10*(#{faqDTO.pageIndex}-1))+1) <= rank AND rank <= (#{faqDTO.pageIndex}*10)
