mapper.select:
SELECT * FROM ( SELECT COUNT(DISTINCT review_sq) OVER() tot_cnt , DENSE_RANK() OVER(ORDER BY review_sq) rank , r.review_content, r.member_sq, r.review_sq, b.branch_tp_id, b.branch_id, b.branch_nm, r.review_title, c.class_id, c.class_nm, CASE WHEN LENGTH(m.name) = 2 THEN SUBSTR(m.name, 1, 1) || '*' ELSE SUBSTR(m.name, 1, 1) || LPAD('*', LENGTH(m.name) - 2, '*') || SUBSTR(m.name, LENGTH(m.name), 1) END AS name, TO_CHAR(r.date_writingout_dt, 'YYYY.MM.DD')AS date_writingout_dt, r.comment_cnt, r.rating, cbs.open_smst_id, c.class_img , EXTRACT(YEAR FROM date_writingout_dt) writing_year, t.member_sq AS teacher_sq, cbs.class_semester_sq FROM review r LEFT JOIN detail_class d ON r.detail_class_sq = d.detail_class_sq JOIN class_by_semester cbs ON d.class_semester_sq = cbs.class_semester_sq JOIN class c ON cbs.class_id = c.class_id JOIN member m ON r.member_sq = m.member_sq JOIN branch b ON c.branch_id = b.branch_id JOIN teacher t ON c.member_sq = t.member_sq
AND b.branch_id = #{branch_id}
AND (review_title LIKE '%' || #{dto.q} || '%' OR review_content LIKE '%' || #{dto.q} || '%')
ORDER BY date_writingout_dt DESC
ORDER BY rating DESC
) a
WHERE (#{dto.pageIndex}-1)*#{dto.listCnt} + 1 <= rank AND rank <= #{dto.pageIndex}*#{dto.listCnt}

SELECT *
FROM (
        SELECT COUNT(DISTINCT comment_sq) OVER() tot_cnt
             , DENSE_RANK() OVER(ORDER BY comment_sq) rank
             , CASE WHEN LENGTH(m.name) = 2 
            	    THEN SUBSTR(m.name, 1, 1) || '*' 
               ELSE SUBSTR(m.name, 1, 1) || LPAD('*', LENGTH(m.name) - 2, '*') || SUBSTR(m.name, LENGTH(m.name), 1) END AS name, r.review_sq
               , TO_CHAR(rc.write_dt, 'YYYY.MM.DD') AS yy, comment_content, r.teacher_member_sq, r.member_sq, dc.detail_class_sq, cbs.open_smst_id, b.branch_id AS brchCd
        FROM review_comm rc LEFT JOIN review r ON r.review_sq = rc.review_sq
                            LEFT JOIN member m ON rc.member_sq = m.member_sq
                            LEFT JOIN detail_class dc ON dc.detail_class_sq = r.detail_class_sq
                            LEFT JOIN class_by_semester cbs ON dc.class_semester_sq = cbs.class_semester_sq
                            LEFT JOIN class c ON c.class_id = cbs.class_id
                            LEFT JOIN branch b ON b.branch_id = c.branch_id
                            LEFT JOIN teacher t ON t.member_sq = m.member_sq

               	WHERE b.branch_id = #{brchCd}
                AND TO_CHAR(r.date_writingout_dt, 'YYYY')= #{yy}
                AND cbs.open_smst_id = #{lectSmsterCd}
                AND dc.detail_class_sq = #{lectCd}
    			AND r.teacher_member_sq = #{tcCdNo}
				AND r.member_sq = #{mbrNo}
    )

         WHERE (#{dto.pageIndex}-1)*#{dto.listCnt} + 1 <= rank AND rank <= #{dto.pageIndex}*#{dto.listCnt}
