mapper.update:
UPDATE order_detail
SET receipt_status_id=#{receipt_status_id}, cancel_dt=SYSDATE, cancel_amt=#{cancel_amt}, cancel_reason_id=#{cancel_reason_id}
WHERE order_detail_sq=#{order_detail_sq}

UPDATE class_order
SET order_amt=order_amt-#{order_amt}, add_point=add_point-#{add_point}, total_amt=total_amt-#{total_amt}
WHERE order_sq=#{order_sq}

mapper.select:
SELECT COUNT(CASE WHEN receipt_status_id = 4 THEN 1 END)/COUNT(*) cancel_rate
FROM order_detail
WHERE order_sq=#{order_sq}

SELECT co.order_sq, TO_CHAR(co.order_dt, 'YYYY.MM.DD HH24:MI') order_dt, co.order_amt, co.add_point, co.total_amt , od.order_detail_sq, od.children_nm, TO_CHAR(od.cancel_dt, 'YYYY.MM.DD HH24:MI') cancel_dt, od.cancel_amt, ors.receipt_status, ors.receipt_status_id, cr.cancel_reason , dc.detail_class_sq, c.branch_id, b.branch_nm , TO_CHAR(cs.schedule_start_dt, 'YYYY.MM.DD')||' ~ '||TO_CHAR(cs.schedule_end_dt, 'YYYY.MM.DD') ||' ('||DECODE(dc.mon,'Y','월','')||DECODE(dc.tue,'Y','화','')||DECODE(dc.wed,'Y','수','')||DECODE(dc.thu,'Y','목','')||DECODE(dc.fri,'Y','금','')||DECODE(dc.sat,'Y','토','')||DECODE(dc.sun,'Y','일','') ||')' ||SUBSTR(dc.start_time,1,2)||':'||SUBSTR(dc.start_time,3,2)||' ~ '||SUBSTR(dc.end_time,1,2)||':'||SUBSTR(dc.end_time,3,2) ||' /'||dc.class_cnt||'회' class_schedule , TO_CHAR(cs.open_year, 'YYYY') open_year, cs.open_smst_id, cs.class_fee, cs.ex_charge, s.smst_nm, c.class_nm, m.name , c.member_sq FROM class_order co RIGHT JOIN order_detail od ON co.order_sq=od.order_sq LEFT JOIN order_receipt_status ors ON od.receipt_status_id = ors.receipt_status_id LEFT JOIN detail_class dc ON od.detail_class_sq = dc.detail_class_sq LEFT JOIN class_by_semester cs ON dc.class_semester_sq = cs.class_semester_sq LEFT JOIN class c ON cs.class_id = c.class_id LEFT JOIN member m ON c.member_sq = m.member_sq LEFT JOIN branch b ON c.branch_id = b.branch_id LEFT JOIN semester s ON cs.open_smst_id = s.open_smst_id LEFT JOIN cancel_reason cr ON od.cancel_reason_id = cr.cancel_reason_id WHERE co.member_sq = #{member_sq}
AND co.order_sq = #{order_sq}
AND ors.receipt_status_id NOT IN (4)
AND TO_CHAR(cs.open_year, 'YYYY') = #{frmSearchDTO.yy} AND cs.open_smst_id = #{frmSearchDTO.lectSmsterCd}
AND ( INSTR(c.class_nm, #{frmSearchDTO.q}) > 0 OR INSTR(TO_CHAR(co.order_sq),#{frmSearchDTO.q}) > 0 )
ORDER BY co.order_sq DESC
