mapper.select:
SELECT review_content, member_sq, review_sq, branch_tp_id, branch_id, branch_nm, review_title, class_id, class_nm, name, rating, date_writingout_dt, comment_cnt, open_smst_id, class_img FROM ( SELECT r.review_content, r.member_sq, r.review_sq, b.branch_tp_id, b.branch_id, b.branch_nm, r.review_title, c.class_id, c.class_nm, CASE WHEN LENGTH(m.name) = 2 THEN SUBSTR(m.name, 1, 1) || '*' ELSE SUBSTR(m.name, 1, 1) || LPAD('*', LENGTH(m.name) - 2, '*') || SUBSTR(m.name, LENGTH(m.name), 1) END AS name, r.date_writingout_dt, r.comment_cnt, r.rating, cbs.open_smst_id, c.class_img FROM review r LEFT JOIN detail_class d ON r.detail_class_sq = d.detail_class_sq JOIN class_by_semester cbs ON d.class_semester_sq = cbs.class_semester_sq JOIN class c ON cbs.class_id = c.class_id JOIN member m ON r.member_sq = m.member_sq JOIN branch b ON c.branch_id = b.branch_id
AND b.branch_id = #{branch_id}
AND (review_title LIKE '%' || #{q} || '%' OR review_content LIKE '%' || #{q} || '%')
ORDER BY date_writingout_dt DESC
ORDER BY rating DESC
) a
) WHERE (#{reviewDTO.pageIndex}-1)*#{reviewDTO.listCnt} + 1 <= rank AND rank <= #{reviewDTO.pageIndex}*#{reviewDTO.listCnt}
